{"type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega/v3.json",
  "description": "An interactive world map supporting pan and zoom.",
  "autosize": "none",
  "background": "#0c2b40",
  "padding" : 60,
  "signals": [
    {"name": "Tracks", "value": true, "bind": {"input": "checkbox"}},
    {"name": "Forbidden", "value": true, "bind": {"input": "checkbox"}},
    {"name": "tx", "update": "width / 2"},
    {"name": "ty", "update": "height / 2"},
    {
      "name": "scale",
      "value": 500,
      "on": [
        {
          "events": {"type": "wheel", "consume": true},
          "update": "clamp(scale * pow(1.0025, -event.deltaY * pow(16, event.deltaMode)), 500, 5000)"
        }
      ]
    },
    {
      "name": "angles",
      "value": [0, 0],
      "on": [{"events": "mousedown", "update": "[rotateX, centerY]"}]
    },
    {
      "name": "cloned",
      "value": null,
      "on": [{"events": "mousedown", "update": "copy('projection')"}]
    },
    {
      "name": "start",
      "value": null,
      "on": [{"events": "mousedown", "update": "invert(cloned, xy())"}]
    },
    {
      "name": "drag",
      "value": null,
      "on": [
        {
          "events": "[mousedown, window:mouseup] > window:mousemove",
          "update": "invert(cloned, xy())"
        }
      ]
    },
    {
      "name": "delta",
      "value": null,
      "on": [
        {
          "events": {"signal": "drag"},
          "update": "[drag[0] - start[0], start[1] - drag[1]]"
        }
      ]
    },
    {
      "name": "rotateX",
      "value": 0,
      "on": [{"events": {"signal": "delta"}, "update": "angles[0] + delta[0]"}]
    },
    {
      "name": "centerY",
      "value": 50,
      "on": [
        {
          "events": {"signal": "delta"},
          "update": "clamp(angles[1] + delta[1], -60, 60)"
        }
      ]
    }
  ],
  "projections": [
    {
      "name": "projection",
      "type": "mercator",
      "scale": {"signal": "scale"},
      "rotate": [{"signal": "rotateX"}, 0, 0],
      "center": [0, {"signal": "centerY"}],
      "translate": [{"signal": "tx"}, {"signal": "ty"}]
    }
  ],
  "data": [
    {
      "name": "world",
      "url": {"index": "world-map*", "body": {"query": {"bool": {}}}},
      "format": {
        "type": "topojson",
        "property": "hits.hits['0']._source",
        "feature": "countries"
      }
    },
    {"name": "graticule", "transform": [{"type": "graticule", "step": [5, 5]}]},
    {
      "name": "tracks",
      "url": {
        "index": "airspace-report-*",
        "body": {
          "query": {
            "bool": {
              "must": [
                "%dashboard_context-must_clause%",
                {"range": {"@timestamp": {"%timefilter%": true}}}
              ],
              "must_not": ["%dashboard_context-must_not_clause%"],
              "filter": []
            }
          }
        },
        "size": 10000
      },
      "format": {"type": "json", "property": "hits.hits"},
      "transform": [
        {
          "type": "geopoint",
          "projection": "projection",
          "fields": ["_source.coord.longitude", "_source.coord.latitude"]
        },
        {
          "type": "project",
          "fields": [
            "_source.coord.latitude",
            "_source.coord.longitude",
            "_source.coord.altitude",
            "x",
            "y"
          ],
          "as": ["longitude", "latitude", "altitude", "x", "y"]
        }
      ]
    },
    {
      "name": "radar",
      "url": {
        "index": "radars-data*",
        "body": {"query": {"bool": {}}},
        "size": 10000
      },
      "format": {"type": "json", "property": "hits.hits"},
      "transform": [
        {
          "type": "geopoint",
          "projection": "projection",
          "fields": ["_source.coord.longitude", "_source.coord.latitude"]
        }
      ]
    },
    {
      "name": "forbiddenArea",
      "url": {"index": "forbidden-map*", "body": {"query": {"bool": {}}}},
      "format": {"type": "json", "property": "hits.hits['0']._source.values"}
    }
  ],
  "scales": [
    {
      "name": "color",
      "type": "linear",
      "range": {"scheme": "yelloworangered"},
      "domain": {"data": "tracks", "field": "altitude"},
      "zero": true,
      "nice": true
    }
  ],
  "legends": [
    {
      "fill": "color",
      "type": "gradient",
      "orient": "bottom-left",
      "title": "Altitude (m)",
      "labelFontSize": 15,
      "fillColor": "#000000",
      "cornerRadius": 10,
      "direction": "horizontal",
      "titlePadding": 10,
      "labelOffset": 10,
      "padding": 10,
      "titleColor": "#FFFFFF",
      "labelColor": "#FFFFFF"
    }
  ],  
  "marks": [
    {
      "type": "shape",
      "from": {"data": "graticule"},
      "encode": {
        "enter": {
          "strokeWidth": {"value": 1},
          "stroke": {"value": "#000000"},
          "fill": {"value": null}
        }
      },
      "transform": [{"type": "geoshape", "projection": "projection"}]
    },
    {
      "type": "shape",
      "from": {"data": "world"},
      "encode": {
        "enter": {
          "strokeWidth": {"value": 1},
          "stroke": {"value": "#ffffff"},
          "fill": {"value": "#abd4ef"}
        }
      },
      "transform": [{"type": "geoshape", "projection": "projection"}]
    },
    {
      "type": "shape",
      "from": {"data": "forbiddenArea"},
      "encode": {
        "update": {
          "strokeWidth": {"value": 2.5},
          "opacity": {"signal": "Forbidden == true ? 0.4 : 0.0"},
          "stroke": {"value": "#44C41C"},
          "fill": {"value": "#44C41C"}
        },
        "hover": {}
      },
      "transform": [{"type": "geoshape", "projection": "projection"}]
    },
    {
      "type": "symbol",
      "from": {"data": "tracks"},
      "encode": {
        "enter": {
          "shape": "circle",
          "size": {"signal": "0.1*scale"},
          "fill": {"scale": "color", "field": "altitude"},
          "opacity": {"value": 1},
          "tooltip": {
            "signal": "{'Longitude': datum.longitude,'Latitude': datum.latitude,'altitude': datum.altitude}"
          }
        },
        "update": {
          "size": {"signal": "0.1*scale"},
          "x": {"field": "x"},
          "y": {"field": "y"},
          "opacity": {"signal": "Tracks == true ? 0.7 : 0.0"}
        },
        "hover": {"size": {"signal": "2*scale"}}
      }
    }
  ]
}"},"title":"[AIRSPACE] Tracks monitoring"}

{"type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega/v3.json",
  "description": "A population pyramid showing U.S. demographics from 1850 to 2000.",
  "autosize": "fit",
   "padding" : 10,

  "signals": [
    {
      "name" : "mouseClick",
      "value" : 0,
      "on": [
        {
          "events" : "window:mousedown",
          "update" : "x()"
        }
      ]
    },
    {
      "name" : "scroll",
      "update": "[0, width]",
      "on": [
        {
          "events" : {"type" : "wheel" "consume": true},
          "update" : "clampRange([(scroll[0] + (event.wheelDelta/5)),  (scroll[1] - (event.wheelDelta/5))], 0, width)"
        },
        {
          "events" : "[window:mousedown, window:mouseup] > window:mousemove",
          "update" : "clampRange([(scroll[0] + (mouseClick - x())),  (scroll[1] + (mouseClick - x()) )], 0, width)"
        }        
      ]
    },
    {
      "name": "xDomain",
       "on": [
         {
           "events" : {"signal": "scroll"},
           "update" : "[invert('xDateBaseline', scroll[0]), invert('xDateBaseline', scroll[1])]"
         }

       ]
    }

  ],  

  "data": [
    {
      "name": "altitude_values",
      "url": {
        "index": "airspace-report-*",
        "body": {
          "query": {
            "bool": {
              "must": [
                "%dashboard_context-must_clause%",
                {"range": {"@timestamp": {"%timefilter%": true}}}
              ],
              "must_not": ["%dashboard_context-must_not_clause%"],
              "filter": []
            }
          }
        },
        "size": 10000
      },
      "format": {"type": "json", "property": "hits.hits"},
      "transform": [
        {
          "type": "project",
          "fields": [
            "_source.@timestamp",
            "_source.coord.altitude",
            "_source.tracks.id"
          ],
          "as": ["date", "altitude", "id"]
        },
         {
           "type" : "formula",
           "expr" : "timeParse(datum.date, '%Y-%m-%dT%H:%M:%S.%LZ')",
           "as" : "date"
         }        
      ]
    },  
    {
      "name": "altitude",
       "values" : [
          {
            "x" : 0,
            "y" : 2000,
            "y2" : 2000

          },
          {
            "x" : 0,
            "y" : 4000,
            "y2" : 4000
          },
          {
            "x" : 0,
            "y" : 6000,
            "y2" : 6000

          } ,
          {
            "x" : 0,
            "y" : 8000,
            "y2" : 8000
          },
          {
            "x" : 0,
            "y" : 10000,
            "y2" : 10000
          }          
       ],
       "transform": [
         {
           "type" : "formula",
           "expr" : "timeParse(datum.date, '%Y-%m-%dT%H:%M:%S.%LZ')",
           "as" : "date"
         }
       ]      
    }    
  ],
  "scales": [
    {
      "name": "color",
      "type": "ordinal",
      "domain": {"data": "altitude", "field": "y"},
      "range": {"scheme": "yelloworangered"}
    },  
    {
      "name": "color_track_id",
      "type": "ordinal",
      "domain": {"data": "altitude_values", "field": "id"},
      "range": {"scheme": "purplered"}
    },        
    {
      "name": "xDateBaseline",
      "type": "time",
      "range": "width",
      "domain": {"data": "altitude_values", "field": "date"}    
    },    
    {
      "name": "xDate",
      "type": "time",
      "range": "width",
      "domain": {"data": "altitude_values", "field": "date"},
      "domainRaw" : {"signal": "xDomain"},
      "nice": true, "zero": false
    },
    {
      "name": "yAltitude",
      "type": "linear",
      "range": "height",
      "domain": {"data": "altitude_values", "field": "altitude"},
      "nice": true, "zero": true
    }
  ],
  "axes": [
    {"orient": "bottom", "scale": "xDate", "labelAngle" : {"value" : 45},
    "grid": false, "gridColor" : "#00000",
    "labelAlign" : "left", "tickColor" : {"value" :"#FFFFFF"},
    "labelColor" : "#FFFFFF", "labelFontSize" : 15, "title" : "Timestamp", "titleColor" : "#FFFFFF", "titleFontSize" : 20, "titlePadding" : 10, "labelPadding" : 10},

    {"orient": "left", "scale": "yAltitude", "labelColor" : "#FFFFFF", "labelFontSize" : 15, "title" : "Altimeter", "titleColor" : "#FFFFFF", "titleFontSize" : 20, "titlePadding" : 10, "domainOpacity"	: 0, "labelPadding" : 10}
  ],
  "marks": [
    {
      "type": "rule",
      "from" : {"data": "altitude"},
      "encode": {
        "enter": {
          "stroke": {"scale": "color", "field" : "y"}
        },
        "update": {
          "x": {"field": "x"},
          "y": {"scale" : "yAltitude", "field": "y"},
          "x2": {"signal": "width"},
          "y2": {"scale" : "yAltitude", "field": "y2"},
          "strokeWidth": {"value": "3"},
          "strokeDash": {"value": [0, 1, 1]},
          "fillOpacity" : {"value" : 1}	

        }
      }
    },
  {
    "name": "altimeter",
    "from" : {"data": "altitude_values"},
    "type": "symbol",
    "encode": {
      "enter" : {
      "interpolate": {"value": "monotone"},
      "x" : {"scale" : "xDate", "field" : "date"},
      "y" : {"scale" : "yAltitude", "field" : "altitude"},
      "size" : {"value" : 300},
      "stroke": {"scale": "color_track_id", "field" : "id"},
      "strokeWidth": {"value": "3"}
      },
      "update" : {
      "x" : {"scale" : "xDate", "field" : "date"},
      "y" : {"scale" : "yAltitude", "field" : "altitude"}        
      }
    }
  }

  ]  
}"},"title":"[AIRSPACE] ALTIMETER"}

{"type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega/v4.json",
  "width": 1000,
  "height": 700,
  "autosize": "none",
  "signals": [
    {"name": "fullCircle", "update": "2*PI"},
    {"name": "splitNumber", "value": 16},
    {"name": "circleSizeInner", "update": "400000"},
    {"name": "circleRayonInner", "value": "320"},
    {"name": "circlePos", "update": "[width/2, height/2]"},
    {
      "name": "distance_max",
      "value": 1000,
      "bind": {"input": "range", "min": 0, "max": 1000, "step": 1}
    }
  ],
  "data": [
    {
      "name": "radar",
      "values": [
        {"id": 1},
        {"id": 2},
        {"id": 3},
        {"id": 4},
        {"id": 5},
        {"id": 6},
        {"id": 7},
        {"id": 8},
        {"id": 9},
        {"id": 10},
        {"id": 11},
        {"id": 12},
        {"id": 13},
        {"id": 14},
        {"id": 15},
        {"id": 16}
      ],
      "transform": [
        {
          "type": "formula",
          "expr": "circleRayonInner * cos(datum.id * (fullCircle / splitNumber)) + circlePos[0]",
          "as": "x"
        },
        {
          "type": "formula",
          "expr": "circleRayonInner * sin(datum.id * (fullCircle / splitNumber)) + circlePos[1]",
          "as": "y"
        }
      ]
    },
    {
      "name": "plots",
      "url": {
        "index": "radar-report*",
        "body": {
          "query": {
            "bool": {
              "must": [
                "%dashboard_context-must_clause%",
                {"range": {"@timestamp": {"%timefilter%": true}}}
              ],
              "must_not": ["%dashboard_context-must_not_clause%"],
              "filter": []
            }
          }
        },
        "size": 10000
      },
      "format": {"type": "json", "property": "hits.hits"},
      "transform": [
        {
          "type": "formula",
          "expr": "datum._source.coord.degree*PI/180",
          "as": "radian"
        },
        {
          "type": "project",
          "fields": [
            "radian",
            "_source.coord.distance"
          ],
          "as": ["radian", "distance"]
        }
      ]
    }
  ],
  "scales": [
    {
      "name": "distanceBound",
      "type": "linear",
      "range": [0, {"signal": "circleRayonInner"}],
      "domain": {"data": "plots", "field": "distance"}
    },
    {
      "name": "color",
      "type": "linear",
      "domain": {"data": "plots", "field": "distance"},
      "range": {"scheme": "purplered"},
      "reverse": false,
      "zero" : true      
    }
  ],
  "legends": [
    {
      "fill": "color",
      "type": "gradient",
      "orient": "bottom-left",
      "title": "Distance (m)",
      "labelFontSize": 15,
      "fillColor": "#000000",
      "cornerRadius": 10,
      "direction": "horizontal",
      "titlePadding": 10,
      "labelOffset": 10,
      "padding": 10,
      "titleColor": "#FFFFFF",
      "labelColor": "#FFFFFF"
    }
  ],    
  "marks": [
    {
      "type": "symbol",
      "name": "outer-schema",
      "encode": {
        "enter": {
          "xc": {"signal": "circlePos[0]"},
          "yc": {"signal": "circlePos[1]"},
          "size": {"signal": "circleSizeInner + 30000"},
          "fillOpacity": {"value": 0.2},
          "fill": {"value": "#4EB78A"},
          "stroke": {"value": "#287956"},
          "strokeWidth": {"value": 10},
          "strokeOpacity": {"value": 0.5}
        }
      }
    },
    {
      "type": "symbol",
      "name": "inner-schema",
      "encode": {
        "enter": {
          "xc": {"signal": "circlePos[0]"},
          "yc": {"signal": "circlePos[1]"},
          "size": {"signal": "circleSizeInner"},
          "fillOpacity": {"value": 0.2},
          "fill": {"value": "#4EB78A"},
          "stroke": {"value": "#287956"},
          "strokeWidth": {"value": 10},
          "strokeOpacity": {"value": 0.5}
        }
      }
    },
    {
      "type": "rule",
      "name": "inner-inner-schema",
      "from": {"data": "radar"},
      "encode": {
        "enter": {
          "x": {"field": "x"},
          "y": {"field": "y"},
          "x2": {"signal": "circlePos[0]"},
          "y2": {"signal": "circlePos[1]"},
          "opacity": {"value": 0.3},
          "stroke": {"value": "#287956"}
        }
      }
    },
    {
      "type": "symbol",
      "name": "points",
      "from": {"data": "plots"},
      "encode": {
        "enter": {
          "xc": {
            "signal": "scale('distanceBound', datum.distance) * cos(datum.radian) + circlePos[0]"
          },
          "yc": {
            "signal": "scale('distanceBound', datum.distance) * sin(datum.radian) + circlePos[1]"
          },
          "size": {"value": "300"},
          "fillOpacity": {"value": 0.8},
          "fill": {"scale": "color", "field": "distance"},
          "stroke": {"scale": "color", "field": "distance"},
          "strokeWidth": {"value": 5},
          "strokeOpacity": {"value": 0.5}
        },
        "update" : {
             "fillOpacity": {"signal": "datum.distance >  distance_max  ? 0 : 0.8"},
             "strokeOpacity": {"signal": "datum.distance >  distance_max ? 0 : 0.8"},

        }
      }
    }
  ]
}"},"title":"[RADAR] Distance Coverage"}

